CommonData.BannerCustomCSS:
body.appMode #onetrust-banner-sdk {
  display: none !important;
}

#onetrust-banner-sdk p {
  font-family: "AzoSansRegular", AzoSans, 'azo-sans' !important;
  font-size: 80pt;
}
.ot-pc-footer-logo {
  visibility: hidden;
}
#onetrust-policy-title {
  text-align: center !important;
  padding-left:0px!important;
  padding-right:0px!important;
}
#onetrust-button-group {
  text-align: left !important;
  margin-bottom:20px!important;
  display: flex !important;
  flex-direction: column !important;
  align-items: start !important;
}
.banner-actions-container {
  float: none !important;
  width: 100% !important;
  text-align: center !important;
  display: contents !important;
/*margin-bottom: 42px !important;*/
}
#onetrust-policy-title {
  font-size: 20px !important;
  font-family: "AzoSansMedium", AzoSans, 'azo-sans' !important;
}
#onetrust-banner-sdk #onetrust-policy-text p {
margin-bottom:15px!important;
}
#onetrust-policy-text,
#onetrust-pc-btn-handler,
#onetrust-accept-btn-handler,
#onetrust-reject-all-handler  {
  font-size: 16px !important;
  font-family: "AzoSansRegular", AzoSans, 'azo-sans' !important;
  line-height: 21px!important;
}
#onetrust-pc-btn-handler {
    color: #ffffff!important;
    background-color: #30a74b!important;
    border-color: #30a74b!important;
    text-decoration: none!important;
    border-radius: 4px !important;
    padding: 12px 12px !important;
    width: 100% !important;
    height: 70px !important;
    font-weight: 600!important;
    margin: 0!important;
    position: relative!important;
    max-width: 100%!important;
   margin-bottom: 1.1rem !important;
}
#onetrust-accept-btn-handler {
  border-radius: 4px !important;
  padding: 12px 12px !important;
  width: 100% !important;
  height: 70px !important;
}
#onetrust-reject-all-handler {
  order: 3 !important;
  border-radius: 4px !important;
  padding: 12px 12px !important;
  width: 100% !important;
  height: 70px !important;
  margin-bottom:1.5rem !important;  
}
#onetrust-banner-sdk #onetrust-policy-title {
  width: calc(100% - 44px) !important;
  padding: 0 0 5px 30px  !important;
}
.ot-close-icon {
  opacity: 0.75 !important;
}
.ot-close-icon:focus {
  opacity: 0.75 !important;
}
#onetrust-banner-sdk .ot-close-icon {
  border: none !important;
}
#onetrust-close-btn-container button {
  margin-bottom: 0px !important;
  position: absolute;
}

#onetrust-banner-sdk #onetrust-close-btn-container {
  margin-right: 15px !important;
  margin-left: 15px !important;
}

#onetrust-banner-sdk .cookie-setting-link:focus {
  outline: none !important;
}

#onetrust-consent-sdk #onetrust-banner-sdk *:focus,
#onetrust-consent-sdk #onetrust-banner-sdk:focus {
  outline: none !important;
}

#onetrust-consent-sdk *, #onetrust-consent-sdk *::before, #onetrust-consent-sdk *::after{
box-sizing:border-box !important;
}

#onetrust-banner-sdk #onetrust-policy-text a{
  margin-left:0px!important;
  color:#000000!important;
  font-weight:bold;
  font-family: "AzoSansMedium", AzoSans, 'azo-sans' !important;
}

.ot-sdk-row{
  max-width: 660px!important;
  margin-left:auto!important;
  margin-right:auto!important;
}

#onetrust-banner-sdk.otCenterRounded {
  width:90%!important;
  max-width:1600px!important;
}

#onetrust-consent-sdk button{
   font-weight:500!important;
}

@media only screen and (max-width: 425px) {
  #onetrust-banner-sdk.otCenterRounded {
    top: 50px !important;
    transform: none !important;
    -webkit-transform: none !important;
    bottom: 5px;
    height: fit-content;
  }
  #onetrust-button-group-parent {
    margin-top: 10px !important;
    font-family: "AzoSansRegular", AzoSans, 'azo-sans' !important;
  }
  #onetrust-policy-text {
    margin-top: 10px !important;
  }
#onetrust-banner-sdk #onetrust-pc-btn-handler {
 margin-bottom: 0.7rem !important;
}
}

@media only screen and (min-width: 321px) and (max-width: 599px) {
  #onetrust-banner-sdk #onetrust-policy-text,
  #onetrust-banner-sdk :not(.ot-dpd-desc) > .ot-b-addl-desc,
  #onetrust-banner-sdk .ot-dpd-container {
    width: calc(100% - 72px) !important;
  }
  #onetrust-banner-sdk #onetrust-button-group-parent {
     padding: 0px 16px !important;
  }
}

@media only screen and (min-width: 400px) and (max-width: 599px) {
  #onetrust-banner-sdk #onetrust-button-group-parent {
    padding: 15px 36px !important;
  }
}

@media only screen and (min-width: 375px) and (max-width: 599px) {
  #onetrust-policy-text {
    margin: 10px 36px 0px 36px !important;
  }
}

@media only screen and (max-width: 600px) {
#onetrust-banner-sdk #onetrust-policy-title {
    padding: 0 0px 5px 35px !important;
}
#onetrust-banner-sdk #onetrust-pc-btn-handler {
 margin-bottom: 0.7rem !important;
}
}

@media only screen and (min-width: 700px) {
  #onetrust-button-group-parent {
    margin-top: 0px !important;
  }
  #onetrust-policy-text {
    margin: 10px 68px 0px 68px !important;
  }
  #onetrust-banner-sdk #onetrust-button-group-parent {
    padding: 15px 68px !important;
  }
  #onetrust-button-group {
    margin-bottom:0px!important;
  }
}
@media only screen and (max-width: 700px) and (min-width: 426px) {
  #onetrust-banner-sdk.otCenterRounded {
    top: 15% !important;
  }
}
